data.content:
/* 404 页 building 页风格 */

* {
    box-sizing: border-box;
}
body {
    margin: 0;
}
#ihnhp2{
    text-align:center;
    font-size:72px;
    font-family:Tahoma, Geneva, sans-serif;
}
.mb-3{
    text-align:center;
    font-size:28px;
}
.img-fluid{
    width:243.58697558593747px;
    height:96px;
}
.mb-10{
    text-align:center;
}
.text_align{
    text-align:center;
    font-size:20px;
}
.button {
    background-color: #4CAF50; /* Green */
    border: none;
    color: white;
    padding: 7px 18px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16px;
    border-radius:50rem!important;
    margin: 0 4px;
}
.text-center{text-align:center!important}
.col-lg-8{flex:0 0 auto;width:66.66666667%}
.col-xl-7{flex:0 0 auto;width:58.33333333%}
.col-xxl-6{flex:0 0 auto;width:50%}
.mx-auto{margin-right:auto!important;margin-left:auto!important}
.btn-primary{color:#fff;background-color:#3f78e0;border-color:#3f78e0}
.rounded-pill{border-radius:50rem!important}
.row{--bs-gutter-x:1.5rem;--bs-gutter-y:0;display:flex;flex-wrap:wrap;margin-top:calc(-1 * var(--bs-gutter-y));margin-right:calc(-.5 * var(--bs-gutter-x));margin-left:calc(-.5 * var(--bs-gutter-x))}.row>*{flex-shrink:0;width:100%;max-width:100%;padding-right:calc(var(--bs-gutter-x) * .5);padding-left:calc(var(--bs-gutter-x) * .5);margin-top:var(--bs-gutter-y)}
.h3{
    font-weight: bold;
    font-size: 25px;
    text-align: center;
}
.h1{
    font-weight: bold;
    font-size: 45px;
    text-align: center;
}

