            removeExistingAIDiv(); // 如果URL不匹配了，移除可能存在的旧AI框
            return false; // URL不匹配，检查不通过
        }
    } catch (error) {
        console.error("【AI 摘要前端】我没有看懂你编写的自定义链接规则...", error);
        return false; // 出错，检查不通过
    }
}

