                    data[key] = []
                    continue
                # A line like 'attribute: >-', which means that it uses Line Folding.
                elif value == '>-':
                    # A mark to indicate that we should treat the next line as one of the parameter of the attribute.
                    line_fold_attribute = key
                    data[key] = ''
                    continue
